mapper.update:
UPDATE facility
SET id = #{name},
    level = #{level},
    manager_id = #{managerId},
    manager_name = #{managerName},
    facility_name = #{facilityName},
    facility_address = #{facilityAddress}
    is_active = #{isActive}
WHERE id = #{id}

UPDATE facility
SET is_active = 0
WHERE id = #{id}
